common = require('common');

exports.OPTIONS = function(path) {
    return function(done) {
        common.req({
            method: 'OPTIONS',
            path: path,
            data: '',
            headers: {
                Origin: 'https://linkeddata.github.com',
            },
        },
        function(r) {
            this.response = r;
            done();
        }.bind(this));
    };
}

exports.HEAD = function(path, headers) {
    return function(done) {
        common.req({
            method: 'HEAD',
            path: path,
            data: '',
            headers: headers || {},
        },
        function(r) {
            this.response = r;
            done();
        }.bind(this));
    };
}
